  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part part = {props.array[0]} exercise = {props.array[1]}/>
      <Part part = {props.array[2]} exercise = {props.array[3]}/>
      <Part part = {props.array[4]} exercise = {props.array[5]}/>
    </div>
  )
}

const Part = (props) => {
  return(
    <div>
      {props.part} {props.exercise}
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      Number of exercises {props.exercise}
    </div>
  )
}

